//Tibo vanheulepackage sample;import javafx.application.Platform;import javafx.fxml.FXMLLoader;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.control.Label;import javafx.scene.control.MenuButton;import javafx.stage.Stage;import javafx.stage.StageStyle;import sample.about.aboutController;import java.io.IOException;import java.time.LocalDateTime;import java.time.format.DateTimeFormatter;public class Controller {    public Label day;    public Label date;    public Label time;    public MenuButton menu;    private Stage stage;    public void setStageAndSetupListeners(Stage controller){        this.stage = controller;    }    public void initialize() {        // TODO: 14/03/2018        //zet deze tijden  om in in oproepbare methode        //https://docs.oracle.com/javase/8/docs/api/java/time/format/DateTimeFormatter.html        //de huidige datum        LocalDateTime now = LocalDateTime.now();        DateTimeFormatter formatTime = DateTimeFormatter.ofPattern("H:mm");        //formatteer de datum (dag)        DateTimeFormatter formatDay = DateTimeFormatter.ofPattern("EEEE");        //geef nieuwe waarde aan de label met id:day        //formateer datum        DateTimeFormatter formatDate = DateTimeFormatter.ofPattern("d LLLL");        date.setText(now.format(formatDate));        day.setText(now.format(formatDay));        time.setText(now.format(formatTime));    }    public void exit() {        Platform.exit();        System.exit(0);    }    public void about() {        try {            FXMLLoader loader = new FXMLLoader(getClass().getResource("about/about.fxml"));            Parent root = loader.load();            aboutController controller = loader.getController();            Stage stage = new Stage();            stage.initStyle(StageStyle.UNDECORATED);            Scene scene = new Scene(root, 450, 450);            scene.getStylesheets().add("sample/about/about.css");            stage.setScene(scene);            controller.setStageAndSetupListeners(stage);            stage.show();        } catch (IOException e) {            e.printStackTrace();        }    }    public void maximize() {        if (stage.isMaximized()){            stage.setMaximized(false);        }else{            stage.setMaximized(true);        }    }}